        }
        .form-container h2 {
            margin-bottom: 20px;
            color: #333;
            text-align: center;
        }
        .form-group {
            margin-bottom: 15px;
        }
        .form-group label {
            font-weight: bold;
        }
        .text-danger {
            color: #dc3545;
            font-size: 0.875em;
        }
    </style>

    <div class=\"form-container shadow\">
        <div class=\"card-header\">
            <h2><i class=\"bx bx-edit\"></i> Edit Traitement</h2>
        </div>
        <div class=\"card-body\">
            {{ form_start(form, {'attr': {'novalitype': 'novalitype'}}) }}
                <div class=\"form-group\">
                    {{ form_label(form.type) }}
                    {{ form_widget(form.type, {'attr': {'class': 'form-control'}}) }}
                    <div class=\"text-danger\">
                        {{ form_errors(form.type) }}
                    </div>
                </div>
                <div class=\"form-group\">
                    {{ form_label(form.medicament) }}
                    {{ form_widget(form.medicament, {'attr': {'class': 'form-control'}}) }}
                    <div class=\"text-danger\">
                        {{ form_errors(form.medicament) }}
                    </div>
                </div>
                <div class=\"form-group\">
                    {{ form_label(form.suivi) }}
                    {{ form_widget(form.suivi, {'attr': {'class': 'form-control'}}) }}
                    <div class=\"text-danger\">
                        {{ form_errors(form.suivi) }}
                    </div>
                </div>
                
                <button type=\"submit\" class=\"btn btn-primary form-control\">
                    <i class=\"bx bx-save\"></i> {{ button_label|default('Uptype') }}
                </button>
            {{ form_end(form) }}
        </div>
    </div>
{% endblock %}


